import React from 'react';
import './App.css';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    categories: CATEGORIES, 
    filter: 'All'
  }




  renderButtons(){
    return this.state.categories.map(category =>{
      return<button onClick={()=> this.handleClick(category)}>{category}</button>
    })
  }

  handleClick(cat){
    this.setState({
      filter: cat
    })
  }

  renderTasks(){
    // const filTasks = this.state.tasks.filter(task => task.category === cat)
    // this.renderTasks(filTasks)
    const { filter, tasks } = this.state

    let filTasks = tasks

    if (filter !== 'All'){
      filTasks = filTasks.filter(task => task.category === filter)
    }

    return filTasks.map(task => {
      return (
        <div className="task">
          <div className="label">{task.category}</div>
          <div className="text">{task.text}</div>
        </div>
      )
    })
  }

  render() {
    console.log(this.state)
    return (
      <div className="App">
        <h2>My tasks</h2>
        <div className="categories">
          {this.renderButtons()}
        </div>
        <div className="tasks">
          <h5>Tasks</h5>
          <div>
            {this.renderTasks()}
          </div>
        </div>

      </div>
    );
  }
}


export default App;
