import React from 'react';
import './App.css';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    categories: CATEGORIES
  }

  handleClick(category){
    const filTasks = this.state.tasks.filter(task => task.category === category)
    this.renderTasks(filTasks)

  }


  renderButtons(){
    return this.state.categories.map(category =>{
      return<button onClick={()=> this.handleClick(category)}>{category}</button>
    })
  }
  renderTasks(tasks){
    return tasks.map(task => {
      return (
        <div className="task">
          <div className="label">{task.category}</div>
          <div className="text">{task.text}</div>
        </div>
      )
    })
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <div className="categories">
          {this.renderButtons()}
        </div>
        <div className="tasks">
          <h5>Tasks</h5>
          <div>
            {

            }
          </div>
        </div>

      </div>
    );
  }
}


export default App;
